	const [videoURL, setVideoURL] =
		useState("https://youtu.be/sa9l-dTv9Gk");

	//A function to fetch the required URL
	// and storing it inside the
	// videoURL state variable
	function handleSearch() {
		movieTrailer(video).then((res) => {
			setVideoURL(res);
		});
