        setVideo(moviestitle)
		movieTrailer(video).then((res) => {
			setVideoURL(res);
		});
	}

	return (
        <Fragment>
            <div className='Container'>
            </div>
            <div className='player'>
            <ReactPlayer  url={videoURL} controls={true} />
            </div>
		
        </Fragment>
		
	);
}

export default Trailer;
