import React, { Fragment, useContext, useEffect, useState } from 'react'
import axios from 'axios'
import {AiFillPlayCircle} from 'react-icons/ai'
import noImag from '../images/movie-no-poster.jpg'
import '../styles/Video.css'
import {Container} from './NavBar'

function Movies() {
  const toggle = useContext(Container)
  const [moviesData, setmovesData] = useState([]);
  const Api ='https://api.themoviedb.org/3/discover/movie';
  const Moviecall = async()=>{
    const data = await axios.get( Api, {
      params: {
        api_key :'8ad1381bef9eefb476641c93619f3caf',
      }
    })
    const allData = data.data.results;
    setmovesData(allData)
  }
  useEffect(()=>{
    Moviecall()
  }, [])
  console.log(moviesData)
  return (
    <Fragment>
        { moviesData.map((movie) => {
          return(
            <Fragment >
              <div id='movies-container'>
                  <AiFillPlayCircle color='#ff206e' fontSize={40} id='playIcon' />
                  <img src={(movie.poster_path ? `https://image.tmdb.org/t/p/w500${movie.poster_path}` :  noImag )} alt="" />
                  <h3>{movie.title}</h3>
              </div>
            </Fragment>

          )
        })}
    </Fragment>
  )
}

export default Movies
